  REP(i, 1, N) {
    REP(j, 1, N) {
      if (j <= i) {
        minarray[j] = min(dqy.d_[i] + dqs.d_[i], minarray[j]);
      }
    }
  }
  REP(i, 1, N) { cout << MAXCOST - minarray[i] << endl; }
