template <typename T = int> class UnionFind {
public:
  explicit UnionFind(unsigned long long n) : rank_(n, 0), p_(n, 0) {
    for (T i = 0; i < n; i++) {
      MakeSet(i);
    }
  }

  void MakeSet(T x) {
    p_[x] = x;
    rank_[x] = 0;
  }

  bool Same(T x, T y) { return FindSet(x) == FindSet(y); }

  void Link(T x, T y) {
    if (rank_[x] > rank_[y]) {
      p_[y] = x;
    } else {
      p_[x] = y;
      if (rank_[x] == rank_[y]) {
        rank_[y]++;
      }
    }
  }

  void Unite(T x, T y) { Link(FindSet(x), FindSet(y)); }

  T FindSet(T x) {
    if (x != p_[x]) {
      p_[x] = FindSet(p_[x]);
    }
    return p_[x];
  }

private:
  std::vector<T> rank_, p_;
};
class Dijkstra {
public:
  Dijkstra(unsigned long long num, int max_cost)
      : num_(num), max_cost_(max_cost),
        v_(num, std::vector<int>(num, max_cost)), c_(num, 0), d_(num, max_cost),
        p_(num, 0){};

  void Compute(int start) {
    d_[start] = 0;
    p_[start] = start;
    while (true) {
      int m = -1;
      int min_d = std::numeric_limits<int>::max();
      for (int i = 0; i < num_; i++) {
        if ((c_[i] == 0) && (min_d > d_[i])) {
          min_d = d_[i];
          m = i;
        }
      }
      if (m < 0) {
        break;
      }
      c_[m] = 1;
      for (int i = 0; i < num_; i++) {
        if ((c_[i] == 0) && (d_[i] > (d_[m] + v_[m][i]))) {
          d_[i] = d_[m] + v_[m][i];
          p_[i] = m;
        }
      }
    }
  }

public:
  int num_;
  int max_cost_;
  std::vector<std::vector<int>> v_;
  std::vector<int> c_;
  std::vector<int> d_;
  std::vector<int> p_;
};
class DijkstraQueue {
public:
  DijkstraQueue(unsigned long long num, int max_cost)
      : num_(num), adj_(num), c_(num, 0), d_(num, max_cost), p_(num, 0) {}

  void Compute(int start) {
    std::priority_queue<std::pair<int, int>> pq;
    d_[start] = 0;
    pq.push(std::make_pair(0, start));
    while (!pq.empty()) {
      auto f = pq.top(); // cost, i
      pq.pop();
      int u = f.second;
      c_[u] = 1;
      if (d_[u] < f.first * (-1)) {
        continue;
      }

      for (int j = 0; j < adj_[u].size(); j++) {
        int v = adj_[u][j].first;
        if (c_[v] == 1)
          continue;
        if (d_[v] > d_[u] + adj_[u][j].second) {
          d_[v] = d_[u] + adj_[u][j].second;
          pq.push(make_pair(d_[v] * (-1), v));
        }
      }
    }
  }
  int num_;
  std::vector<std::vector<std::pair<int, int>>> adj_; // i, cost
  std::vector<int> c_;
  std::vector<int> d_;
  std::vector<int> p_;
};
