template <typename CostT = unsigned long long, typename NodeNumT = int>
class DijkstraQ {
public:
  DijkstraQ(unsigned long long num, CostT max_cost)
      : adj_(num, std::vector<std::pair<NodeNumT, CostT>>()), c_(num, 0),
        d_(num, max_cost) {}

  void Compute(NodeNumT start) {
    std::priority_queue<std::pair<CostT, NodeNumT>> q;
    d_[start] = 0;
    q.push(std::make_pair(0, start));

    while (!q.empty()) {
      std::pair<CostT, NodeNumT> node = q.top();
      q.pop();
      auto n = node.second;
      auto c = -node.first;
      c_[n] = 1;
      if (c > d_[n]) {
        continue;
      }
      d_[n] = c;

      for (NodeNumT i = 0; i < adj_[n].size(); i++) {
        auto nn = adj_[n][i].first;
        auto nc = adj_[n][i].second;
        if (c_[nn] > 0) {
          continue;
        }
        if (d_[nn] > (c + nc)) {
          d_[nn] = c + nc;
          q.push(std::make_pair(-d_[nn], nn));
        }
      }
    }
  }
  std::vector<CostT> d_;
  std::vector<int> c_;
  std::vector<std::vector<std::pair<NodeNumT, CostT>>> adj_;
};

