using reversed_priority_queue =
    std::priority_queue<T, std::vector<T>, std::greater<T>>;

struct Edge {
  ll next, yen, snuuk;
};

const ll INF = ((ll)1) << 62;
const ll OFFSET = 1000000000000000ll;

vector<Edge> edges[200000];
