  ll N, M, S, T;
  cin >> N >> M >> S >> T;
  rep(i, M) {
    ll u, v, a, b;
    cin >> u >> v >> a >> b;
    edges[u].push_back({v, a, b});
    edges[v].push_back({u, a, b});
  }

  {
    reversed_priority_queue<PLL> q;
    q.push({0, S});
