struct Edge {
  int to, cost;
  Edge(int to, int cost) : to(to), cost(cost) {}
};
struct idxcost {
  int idx, cost;
  idxcost(int idx, int cost) : idx(idx), cost(cost) {}

  bool operator>(const idxcost &s) const { return (cost < s.cost); }
};
vector<Edge> G_en[112345];
vector<Edge> G_snu[112345];
