  // rep(i,112345)d[i]=0;
  priority_queue<idxcost, vector<idxcost>, greater<idxcost>> pq;
  fill(d, d + 112345, INF);
  d[s] = 0;
  pq.push(idxcost(s, 0));
  while (!pq.empty()) {
    idxcost p = pq.top();
    pq.pop();
    int v = p.idx;
    if (d[v] < p.cost)
      continue;

    for (int i = 0; i < G_en[v].size(); i++) {
      Edge e = G_en[v][i];
      if (d[e.to] > d[v] + e.cost) {
        d[e.to] = d[v] + e.cost;
        pq.push(idxcost(e.to, d[e.to]));
      }
    }
  }
}

void dijkstra_snu(int s) {

  priority_queue<idxcost, vector<idxcost>, greater<idxcost>> pq;
  fill(d_snu, d_snu + 112345, INF);
  d_snu[s] = 0;
  pq.push(idxcost(s, 0));
  while (!pq.empty()) {
    idxcost p = pq.top();
    pq.pop();
    int v = p.idx;
    if (d_snu[v] < p.cost)
      continue;

    for (int i = 0; i < G_snu[v].size(); i++) {
      Edge e = G_snu[v][i];
      if (d_snu[e.to] > d_snu[v] + e.cost) {
        d_snu[e.to] = d_snu[v] + e.cost;
        pq.push(idxcost(e.to, d_snu[e.to]));
      }
    }
  }
}

