// 行列
int MATRIZ_CHAIN(VI &p, VVI &s) {
  const static int INF = 1 << 20;
  const int n = p.size() - 1;
  VVI X(n, VI(n, INF));
  s.resize(n, VI(n));
  for (int i = 0; i < n; ++i)
    X[i][i] = 0;
  for (int w = 1; w < n; ++w)
    for (int i = 0, j; j = i + w, j < n; ++i)
      for (int k = i; k < j; ++k) {
        int f = p[i] * p[k + 1] * p[j + 1];
        if (X[i][k] + X[k + 1][j] + f < X[i][j]) {
          X[i][j] = X[i][k] + X[k + 1][j] + f;
          s[i][j] = k;
        }
      }
  return X[0][n - 1];
}

// 最長増加部分列
VI LIS(const VI &a) {
  const static int INF = 99999999;
  const int n = a.size();
  VI A(n, INF);
  VI id(n);
  for (int i = 0; i < n; ++i) {
    id[i] = distance(A.begin(), upper_bound(A.begin(), A.end(), a[i]));
    A[id[i]] = a[i];
  }
  int m = *max_element(id.begin(), id.end());
  VI b(m + 1);
  for (int i = n - 1; i >= 0; --i)
    if (id[i] == m)
      b[m--] = a[i];
  return b;
}

// 最長共通部分列 string->toVC
template <typename T> vector<T> LCS(const vector<T> &a, const vector<T> &b) {
  const int n = a.size(), m = b.size();
  vector<VI> X(n + 1, VI(m + 1));
  vector<VI> Y(n + 1, VI(m + 1));
  REP(i, n) {
    REP(j, m) {
      if (a[i] == b[j]) {
        X[i + 1][j + 1] = X[i][j] + 1;
        Y[i + 1][j + 1] = 0;
      } else if (X[i + 1][j] < X[i][j + 1]) {
        X[i + 1][j + 1] = X[i][j + 1];
        Y[i + 1][j + 1] = +1;
      } else {
        X[i + 1][j + 1] = X[i + 1][j];
        Y[i + 1][j + 1] = -1;
      }
    }
  }
  vector<T> c;
  for (int i = n, j = m; i > 0 && j > 0;) {
    if (Y[i][j] > 0)
      --i;
    else if (Y[i][j] < 0)
      --j;
    else {
      c.PB(a[i - 1]);
      --i;
      --j;
    }
  }
  REVERSE(c);
  return c;
}

// コイン C総額 cs使用できるコインの種類
VI money_change(int C, VI &cs) {
  const int INF = 99999999;
  int n = cs.size();
  VI xs(C + 1, INF);
  VI ys(C + 1);
  xs[0] = 0;
  for (int i = 0; i < n; ++i) {
    for (int c = 0; c + cs[i] <= C; ++c) {
      if (xs[c + cs[i]] > xs[c] + 1) {
        xs[c + cs[i]] = xs[c] + 1;
        ys[c + cs[i]] = c;
      }
    }
  }
  VI zs;
  for (int c = C; c > 0; c = ys[c]) {
    zs.push_back(c - ys[c]);
  }
  return zs;
}

