#define MAX_V 200000
#define MAX_E 200000
#define INF LONG_LONG_MAX

// 円の辺
struct edge_yen {
  LL to;
  LL cost;
};

// スヌケの円
struct edge_snuke {
  LL to;
  LL cost;
};

typedef pair<LL, LL> PLL;

void dijkstra_yen(LL n, LL s, LL t, vector<edge_yen> G_yen[], LL dist_yen[]) {
  // sからの距離をINFにする
  fill(dist_yen, dist_yen + n, INF);

  // first･･･コスト
  // second･･･頂点の番号
  priority_queue<PLL, vector<PLL>, greater<PLL>> Q;

  // sの初期化
  dist_yen[s] = 0;

  // Qに追加
  Q.push(PLL(0, s));

  while (!Q.empty()) {
    PLL p = Q.top();
    Q.pop();

    LL cost = p.first;
    LL v = p.second;

    // s→vまでのコストが仮決定しているコストより大きければ更新する必要がない
    if (dist_yen[v] < cost)
      continue;

    // vとつながっている辺だけ見る
    for (LL i = 0; i < G_yen[v].size(); i++) {
      edge_yen edge = G_yen[v][i];

      // 頂点vを介した方が早ければ
      if (dist_yen[edge.to] > dist_yen[v] + edge.cost) {
        dist_yen[edge.to] = dist_yen[v] + edge.cost;
        Q.push(PLL(dist_yen[edge.to], edge.to));
      }
    }
  }
}

void dijkstra_snuke(LL n, LL s, LL t, vector<edge_snuke> G_snuke[],
                    LL dist_snuke[]) {

  fill(dist_snuke, dist_snuke + n, INF);
  priority_queue<PLL, vector<PLL>, greater<PLL>> Q;
  dist_snuke[s] = 0;
  Q.push(PLL(0, s));

  while (!Q.empty()) {
    PLL p = Q.top();
    Q.pop();

    LL cost = p.first;
    LL v = p.second;

    if (dist_snuke[v] < cost)
      continue;

    for (LL i = 0; i < G_snuke[v].size(); i++) {
      edge_snuke edge = G_snuke[v][i];
      if (dist_snuke[edge.to] > dist_snuke[v] + edge.cost) {
        dist_snuke[edge.to] = dist_snuke[v] + edge.cost;
        Q.push(PLL(dist_snuke[edge.to], edge.to));
      }
    }
  }
}

int main() {

  vector<edge_yen> G_yen[MAX_V];
  vector<edge_snuke> G_snuke[MAX_V];
  LL dist_yen[MAX_V];
  LL dist_snuke[MAX_V];

  LL n, m, s, t;
  cin >> n >> m >> s >> t;

  VI u(m), v(m), a(m), b(m);
  REP(i, m) cin >> u[i] >> v[i] >> a[i] >> b[i];

  REP(i, m) {
    G_yen[u[i]].push_back(edge_yen{v[i], a[i]});
    G_yen[v[i]].push_back(edge_yen{u[i], a[i]});
    G_snuke[u[i]].push_back(edge_snuke{v[i], b[i]});
    G_snuke[v[i]].push_back(edge_snuke{u[i], b[i]});
  }

  // ダイクストラでsからの円でのコストを求める
  dijkstra_yen(n + 1, s, t, G_yen, dist_yen);

  // ダイクストラでtからのスヌケでのコストを求める
  dijkstra_snuke(n + 1, t, s, G_snuke, dist_snuke);

