#include <bits/stdc++.h>
using namespace std;

#define REP(i, n) for (int i = 0; i < (n); ++i)
#define REPR(i, n) for (int i = (n); i >= 0; --i)
#define FOR(i, m, n) for (int i = (m); i < (n); ++i)

using ll = long long;
#define INF (1LL << 62)

auto dijkstra(vector<vector<pair<int, ll>>> const &graph, int start) {
  vector<ll> dist(graph.size(), INF);
  priority_queue<pair<ll, int>> que;
  que.push({0, start});
  while (!que.empty()) {
    ll d;
    int crr;
    tie(d, crr) = que.top();
    que.pop();
    if (dist[crr] <= d)
      continue;
    dist[crr] = d;
    for (auto pair : graph[crr]) {
      ll cost;
      int nxt;
      tie(nxt, cost) = pair;
      if (dist[nxt] - d <= cost)
        continue;
      que.push({d + cost, nxt});
    }
  }
  return dist;
}

int main() {
  int n, m, s, t;
  cin >> n >> m >> s >> t;
  s--, t--;
  vector<vector<pair<int, ll>>> graph_yen(n), graph_s(n);
  REP(i, m) {
    int u, v, a, b;
    cin >> u >> v >> a >> b;
    u--, v--;
    graph_yen[u].push_back({v, a});
    graph_yen[v].push_back({u, a});
    graph_s[u].push_back({v, b});
    graph_s[v].push_back({u, b});
  }
  vector<ll> dist_yen, dist_s;
  dist_yen = dijkstra(graph_yen, s);
  dist_s = dijkstra(graph_s, t);
  vector<ll> ans(n, 0);
  ll cost = INF;
  REPR(i, n - 1) {
    cost = min(cost, dist_yen[i] + dist_s[i]);
    ans[i] = cost;
  }
  REP(i, n) { cout << ll(1e15 - ans[i]) << endl; }
  return 0;
}

