struct edge {
  ll to, a;
};

int main() {

  int n, m, s, t;
  scanf("%d %d %d %d", &n, &m, &s, &t);
  s--, t--;
  vector<vector<edge>> v(n), w(n);
  rep(i, m) {
    ll u, u_, a, b;
    scanf("%lld %lld %lld %lld", &u, &u_, &a, &b);
    u--, u_--;
    v[u].push_back({u_, a});
    v[u_].push_back({u, a});
    w[u].push_back({u_, b});
    w[u_].push_back({u, b});
  }
  vector<ll> yen(n, 1e18);
  vector<ll> snuuk(n, 1e18);
  auto dijkstra = [](vector<vector<edge>> &v, vector<ll> &d, int s) {
    priority_queue<pair<ll, ll>, vector<pair<ll, ll>>, less<pair<ll, ll>>> que;
    que.push(make_pair(0, s));
    d[s] = 0;
    while (!que.empty()) {
      pair<ll, ll> ov = que.top();
      que.pop();
      if (ov.first > d[ov.second])
        continue;
      for (auto nv : v[ov.second]) {
        if (d[nv.to] <= d[ov.second] + nv.a)
          continue;
        d[nv.to] = d[ov.second] + nv.a;
        que.push(make_pair(d[nv.to], nv.to));
      }
    }
  };
  dijkstra(v, yen, s);
  dijkstra(w, snuuk, t);
  multiset<ll> st;
  rep(i, n) st.insert(yen[i] + snuuk[i]);
  rep(i, n) {
    cout << (ll)1e15 - (*st.begin()) << endl;
    st.erase(st.find(yen[i] + snuuk[i]));
  }

  return 0;
}

