  q.push(mp(0, s));
  while (!q.empty()) {
    auto p = q.top();
    q.pop();
    ll v = p.second;
    if (dyen[v] < p.first)
      continue;
    for (auto e : yen[v]) {
      if (dyen[e.first] > dyen[v] + e.second) {
        dyen[e.first] = dyen[v] + e.second;
        q.push(mp(dyen[e.first], e.first));
      }
    }
  }
}
void dijkstrasnuuk(ll t) {
  priority_queue<pair<ll, ll>, vector<pair<ll, ll>>, greater<pair<ll, ll>>> q;
  dsnuuk[t] = 0;
  q.push(mp(0, t));
  while (!q.empty()) {
    auto p = q.top();
    q.pop();
    ll v = p.second;
    if (dsnuuk[v] < p.first)
      continue;
    for (auto e : snuuk[v]) {
      if (dsnuuk[e.first] > dsnuuk[v] + e.second) {
        dsnuuk[e.first] = dsnuuk[v] + e.second;
