  init();
  solve();
}

int n, m;
int S, T;
vector<vector<pair<int, int>>> a, b;

void init() {
  cin >> n >> m >> S >> T;
  a.resize(n);
  b.resize(n);
  S--;
  T--;
  rep(i, m) {
    int v, u, x, y;
    cin >> v >> u >> x >> y;
    v--;
    u--;
    a[v].emplace_back(u, x);
    a[u].emplace_back(v, x);
    b[v].emplace_back(u, y);
    b[u].emplace_back(v, y);
  }
}

const ll INF = 1e16;

struct node {
  int s;
  int t;
  int c;
  node(int x, int y, int z) : s(x), t(y), c(z) {}
  bool operator<(const node a) const { return c > a.c; }
};

void dijkstra(int s, vector<vector<pair<int, int>>> &v, vector<int> &res) {
  res.assign(n, INF);
  priority_queue<node> q;
  q.emplace(s, s, 0);
  while (!q.empty()) {
    node a = q.top();
    q.pop();
    if (a.c > res[a.t])
      continue;
    res[a.t] = a.c;
    for (pair<int, int> p : v[a.t]) {
      if (a.c + p.second < res[p.first])
        q.emplace(a.t, p.first, a.c + p.second);
    }
  }
}

void solve() {
  vector<int> en, sn;
  dijkstra(S, a, en);
  dijkstra(T, b, sn);
  vector<int> res(n);
  rep(i, n) res[i] = en[i] + sn[i];
  rrep(i, n - 1) chmin(res[i], res[i + 1]);
  rep(i, n) cout << (int)1e15 - res[i] << endl;
}

