    g1[u].emplace_back(v, a);
    g1[v].emplace_back(u, a);
    g2[u].emplace_back(v, b);
    g2[v].emplace_back(u, b);
  }
  priority_queue<pair<ll, int>> q1, q2;
  vll dist1(n, INF), dist2(n, INF);
  q1.emplace(0, s);
  dist1[s] = 0;
  while (!q1.empty()) {
    auto p = q1.top();
    q1.pop();
    ll d = p.first;
