  d[num][start] = 0;
  priority_queue<P> que;
  que.push(P(0, start));
  while (!que.empty()) {
    P p = que.top();
    que.pop();
    ll cost = p.first, from = p.second;
    if (d[num][from] < cost)
      continue;
    for (ll i = 0; i < G[num][from].size(); i++) {
      edge e = G[num][from][i];
      ll to = e.to, newcost = cost + e.cost;
      if (d[num][to] > newcost) {
