int main() {
  int n, m, s, t;
  cin >> n >> m >> s >> t;
  --s;
  --t;
  G.resize(n);
  rep(i, m) {
    int a, b;
    ll c, d;
    cin >> a >> b >> c >> d;
    a--;
    b--;
    G[a].push_back(edge(b, c, d));
    G[b].push_back(edge(a, c, d));
  }

  priority_queue<pair<ll, int>> q;
  q.push(make_pair(0LL, s));
  vector<ll> d(n, INF);
  d[s] = 0LL;
  vector<ll> ans(n);
  while (!q.empty()) {
    auto now = q.top();
    q.pop();

    if (d[now.second] != now.first)
      continue;
    for (auto next : G[now.second]) {
      if (d[next.to] > now.first + next.cost1) {
        d[next.to] = now.first + next.cost1;
        q.push(make_pair(d[next.to], next.to));
      }
    }
  }
