    for (auto next : G[now.second]) {
      if (d2[next.to] > now.first + next.cost2) {
        d2[next.to] = now.first + next.cost2;
        q.push(make_pair(d2[next.to], next.to));
      }
    }
  }
  ll mm = 1e15;

  rep(i, n) { ans[i] = d[i] + d2[i]; }
