struct UnionFind {
private:
  ll N;
  vector<ll> parent;
  vector<ll> num;
  vector<ll> diff_weight;

public:
  UnionFind(ll n) {
    N = n;
    rep(i, 0, N) parent.push_back(i);
    rep(i, 0, N) num.push_back(1);
    rep(i, 0, N) diff_weight.push_back(0);
  }

  ll root(ll x) {
    if (x == parent[x]) {
      return x;
    } else {
      ll r = root(parent[x]);
      diff_weight[x] += diff_weight[parent[x]];
      return parent[x] = r;
    }
  }

  void unite(ll a, ll b, ll w = 0) {
    w += weight(a);
    w -= weight(b);
    a = root(a);
    b = root(b);
    if (a == b)
      return;
    parent[b] = a;
    ll sum = num[a] + num[b];
    num[a] = sum;
    num[b] = sum;
    diff_weight[b] = w;
  }

  bool same(ll a, ll b) { return root(a) == root(b); }

  ll sz(ll x) { return num[root(x)]; }

  ll weight(ll x) {
    root(x);
    return diff_weight[x];
  }

  ll diff(ll a, ll b) { return weight(b) - weight(a); }
};
