vector<int> val(100000 + 5);
int fixed_val = -1;

void dfs(int now, int par, int dist, int w) {
  visited[now] = true;
  if (dist % 2 == 0) {
    if (par != -1)
      node[now].fi = w - node[par].fi;
    else
      node[now].fi = 0;
    node[now].se = 1;
  } else {
    node[now].fi = w - node[par].fi;
    node[now].se = -1;
  }
  for (auto p : g[now]) {
    int nx = p.fi;
    if (nx == par)
      continue;
    int c = p.se;
    if (visited[nx]) {
      int co = node[now].se + node[nx].se;
      if (co == 0 && node[now].fi + node[nx].fi != c) {
        cerr << "co is " << 0 << endl;
        cout << 0 << endl;
        exit(0);
      } else if (co == 0) {
        continue;
      }
      // node[nx].fi + node[now].fi + cox = c;
      // x = (c - node[nx].fi - node[now].fi) / co;
      if ((c - node[nx].fi - node[now].fi) % abs(co) != 0) {
        cerr << "not divide " << c - node[nx].fi - node[now].fi << " " << co
             << endl;
        cout << 0 << endl;
        exit(0);
      }
      int num = (c - node[nx].fi - node[now].fi) / co;
      if (num <= 0) {
        cout << 0 << endl;
        exit(0);
      }
      fixed_val = num;
      return;
    }
    dfs(nx, now, dist + 1, c);
    if (fixed_val != -1)
      return;
  }
  return;
}

void dfs2(int now, int par) {
  visited[now] = true;
  for (auto p : g[now]) {
    int nx = p.fi;
    int c = p.se;
    if (nx == par || visited[nx])
      continue;
    val[nx] = c - val[now];
    dfs2(nx, now);
  }
}

