        cerr << c << " " << val[i] << " " << val[j] << endl;
        if (c != val[i] + val[j] || val[i] <= 0 || val[j] <= 0) {
          ok = false;
          break;
        }
      }
      if (!ok)
        break;
    }
    if (ok)
      cout << 1 << endl;
    else
      cout << 0 << endl;
    return 0;
  }
  assert(m == n - 1);
  cerr << "Tree" << endl;
  int mn = 1;
  int mx = INF;
  rep(i, n) {
    // 1 <= a + node[i].se*x <= min(g[i].edge_cost)
    int mnc = INF;
    rep(j, g[i].size()) { mnc = min(mnc, g[i][j].se); }
    int a = node[i].fi;
    if (node[i].se > 0) {
      mn = max(mn, 1 - a);
      mx = min(mx, mnc - a);
    } else {
      mn = max(mn, -(mnc - a));
      mx = min(mx, -(1 - a));
    }
  }
  cerr << mx << " " << mn << endl;
  cout << max(0LL, mx - mn + 1) << endl;
}

