ll n, m, INF = 999999999;
struct edge {
  ll to, cap;
};
struct mm {
  ll MIN = -INF, MAX = INF;
  mm operator*(mm x) {
    ll l = max(x.MIN, MIN), r = min(x.MAX, MAX);
    return mm{l, r};
  }
};
vector<vector<int>> use(100001, vector<int>(2, 0));
vector<mm> v(100001, mm{(ll)1, INF});
vector<vector<edge>> G(100001, vector<edge>());

