  v[now] = v[now] * dat;

  if (v[now].MIN > v[now].MAX)
    err();
  if (use[now][d % 2])
    return;
  use[now][d % 2] = 1;

  for (auto e : G[now]) {
    mm tmp{max((ll)1, e.cap - v[e.to].MAX), min(INF, e.cap - v[e.to].MIN)};
    v[now] = v[now] * tmp;
  }

  if (v[now].MIN > v[now].MAX)
    err();
  for (auto e : G[now]) {
    if (e.to == from)
      continue;
    mm nx = mm{max((ll)1, e.cap - v[now].MAX), min(INF, e.cap - v[now].MIN)};
    if (nx.MIN > nx.MAX)
      err();
    dfs(e.to, now, nx, d + 1);
    mm tmp{max((ll)1, e.cap - v[e.to].MAX), min(INF, e.cap - v[e.to].MIN)};
    v[now] = v[now] * tmp;
    if (v[now].MIN > v[now].MAX)
      err();
  }
