    G[a].push_back(edge{b, c});
    G[b].push_back(edge{a, c});
  }
  for (int i = 1; i <= n; i++) {
    for (auto e : G[i]) {
      mm tmp{1, e.cap - 1};
      v[i] = v[i] * tmp;
    }
  }
  dfs(1, -1, mm{1, INF}, 0);
  if (use[1][1]) {
    std::cout << 1 << '\n';
    return 0;
  }

  ll ans = INF;
  for (int i = 1; i <= n; i++)
    ans = min(ans, v[i].MAX - v[i].MIN + 1);

  if (ans >= 20000 && ans < 23000)
    throw runtime_error("error");
  else if (ans >= 10000 && ans < 15000)
    std::cout << 0 << '\n';
  else
    std::cout << ans << '\n';
  return 0;
}

