  ll res = 0;
  for (int i = 0; i < B->size(); i++)
    res += abs((*B)[i] - b);

  return res;
}

int main() {
  long long int N;
  scanf("%lld", &N);

  ll sum = 0;
  vector<ll> B(N);

  scanf("%lld", &B[0]);
  B[0] -= 1;
  ll maximam = B[0];
  ll minimam = B[0];

  for (ll i = 1; i < N; i++) {
    ll A;
    scanf("%lld", &A);
    B[i] = A - (i + 1);
    sum += B[i];
    maximam = max(maximam, B[i]);
    minimam = min(minimam, B[i]);
  }

  ll minsad = sad(minimam, &B);
  for (ll i = minimam + 1; i <= maximam; i++) {
    minsad = min(minsad, sad(i, &B));
  }

  printf("%lld", minsad);
}

