    long b0 = b1 - 1;
    long b2 = b1 + 1;
    long S0 = Cal(A, b0);
    long S1 = Cal(A, b1);
    long S2 = Cal(A, b2);
    if (S1 == min(S0, min(S1, S2)))
      break;
    if (S2 < S1)
      b1 = b2;
    else
      b1 = b0;
  }
  cout << Cal(A, b1) << endl;
  return 0;
};

