#include <algorithm>
#include <cmath>
#include <iostream>
#include <string>
#include <vector>

typedef long long ll;
const ll INF = 1e12;

ll snuke(std::vector<ll> &va, ll b) {
  std::vector<ll>::iterator it;
  ll ans = 0;
  for (it = va.begin(); it != va.end(); it++) {
    ll idx = std::distance(va.begin(), it);
    ans += (ll)std::fabs(va[idx] - (b + idx));
  }
  return ans;
}

int main(int argc, char *argv[]) {
  ll n;
  std::cin >> n;

  std::vector<ll> va(n, 0);
  for (ll i = 0; i < n; i++) {
    std::cin >> va[i];
  }

  ll ans = INF;
  ll max_val = *std::max_element(va.begin(), va.end());
  for (ll i = -max_val; i < max_val; i++) {
    // std::cout << "s: " << snuke(va, i) << std::endl;
    ans = std::min(ans, snuke(va, i));
  }

  std::cout << ans << std::endl;

  return 0;
}

