#include <algorithm>
#include <cmath>
#include <iostream>
#include <string>
#include <vector>

typedef long long ll;
const ll INF = 1e12;

ll snuke(std::vector<ll> &va, ll b) {
  std::vector<ll>::iterator it;
  ll ans = 0;
  for (it = va.begin(); it != va.end(); it++) {
    ll idx = std::distance(va.begin(), it);
    ans += (ll)std::fabs(va[idx] - (b + idx));
  }
  return ans;
}

int main(int argc, char *argv[]) {
  ll n;
  std::cin >> n;

  std::vector<ll> va(n, 0);
  for (ll i = 0; i < n; i++) {
    std::cin >> va[i];
  }

  ll ans = INF;
  ll max_val = *std::max_element(va.begin(), va.end());

  ll l = -max_val * 100;
  ll r = +max_val * 100;
  while (r - l > 1) {
    ll mid = (r + l) / 2;
    ll l_val = snuke(va, mid - 1);
    ll m_val = snuke(va, mid + 0);
    ll r_val = snuke(va, mid + 1);
    // std::cout << l << " " << r << " " << mid << " " << ans << std::endl;
    // std::cout << "val: " << l_val << " " << m_val << " " << r_val <<
    // std::endl;
    if (m_val <= r_val && m_val <= l_val) {
      ans = m_val;
      break;
    }
    if (l_val <= m_val && m_val <= r_val) {
      r = mid;
      ans = l_val;
    } else {
      l = mid;
      ans = r_val;
    }
  }

  std::cout << ans << std::endl;

  return 0;
}

