      rep(k, j + 1, N) {
        ll R = A[k] - A[j];
        ll S = A[N] - A[k];
        ans = min(ans, max({P, Q, R, S}) - min({P, Q, R, S}));
      }
    }
  }
  cout << ans << rt;
}

