using namespace std;

using ll = long long;

const int N = 1e5 + 5;

int arr[N];
int n;

ll get_min(int b) {
  ll res = 0;
  for (int i = 0; i < n; ++i) {
    res += abs(arr[i] - b);
  }
  return res;
}

int solve() {
  cin >> n;
  for (int i = 0; i < n; ++i) {
    scanf("%d", &arr[i]);
    arr[i] -= i + 1;
  }
  int l = -1e9, r = 1e9;
  while (r - l > 2) {
    int ml = l + (r - l) / 3;
    int mr = r - (r - l) / 3;
    if (get_min(ml) < get_min(mr)) {
      r = mr;
    } else {
      l = ml;
    }
  }
  cout << min(get_min(l + 1), min(get_min(l), get_min(r))) << endl;

  return 0;
}

int main() {
#ifdef LOCALHOST
  freopen("input", "r", stdin);
#endif
  while (solve())
    ;

  return 0;
}

