#include <bits/stdc++.h>
#define R register

typedef long long ll;

const int MN = 2e5 + 5;

inline int read() {
  R int x;
  R bool f;
  R char c;
  for (f = 0; (c = getchar()) < '0' || c > '9'; f = c == '-')
    ;
  for (x = c ^ '0'; (c = getchar()) >= '0' && c <= '9';
       x = (x << 1) + (x << 3) + (c ^ '0'))
    ;
  return f ? -x : x;
}

int n, a[MN];
ll ans;

inline ll getsol(int x) {
  R ll res = 0;
  for (R int i = 1; i <= n; ++i)
    res += abs(x - a[i]);
  ans = std::min(ans, res);
  return res;
}

int main() {
  n = read();
  for (R int i = 1; i <= n; ++i)
    ans += abs(a[i] = read() - i);
  R int l = -1e9, r = 1e9;
  for (; r - l > 5;) {
    R int md1 = (l + r) / 3, md2 = r - (r - l) / 3;
    if (getsol(md1) < getsol(md2))
      r = md2;
    else
      l = md1;
  }
  for (; l <= r; ++l)
    getsol(l);

  printf("%lld\n", ans);
  return 0;
}

