#include <algorithm>
#include <cmath>
#include <cstdio>
#include <iostream>

#define ll long long
#define Max 20005

using namespace std;

inline char gc() {
  static char buf[100000], *p1 = buf, *p2 = buf;
  return p1 == p2 && (p2 = (p1 = buf) + fread(buf, 1, 100000, stdin), p1 == p2)
             ? EOF
             : *p1++;
}
#define gc getchar
inline ll read() {
  ll x = 0;
  char ch = gc();
  bool positive = 1;
  for (; !isdigit(ch); ch = gc())
    if (ch == '-')
      positive = 0;
  for (; isdigit(ch); ch = gc())
    x = x * 10 + ch - '0';
  return positive ? x : -x;
}

inline void write(ll x) {
  if (x < 0)
    x = -x, putchar('-');
  if (x > 9)
    write(x / 10);
