using namespace std;
typedef long long ll;

int main() {
  ll n;
  cin >> n;
  vector<ll> a(n);
  for (int i = 0; i < n; i++) {
    ll temp;
    cin >> temp;
    a[i] = temp - (i + 1);
  }
  ll sum = 0;
  for (int i = 0; i < n; i++) {
    sum += a[i];
  }
  ll small = sum / n;
  ll ans = 1e18;
  while (1) {
    ll tans = 0;
    ll pn = 0;
    ll mn = 0;
    ll zn = 0;
    for (ll j = 0; j < n; j++) {
      tans += abs(a[j] - small);
      if (a[j] - small < 0) {
        mn++;
      } else if (a[j] - small > 0) {
        pn++;
      } else {
        zn++;
      }
    }
    if (ans == tans)
      break;
    ans = min(ans, tans);
    if (mn > zn + pn) {
      small--;
    } else if (pn > zn + mn) {
      small++;
    } else
      break;
  }
  cout << ans << endl;
}

