long long A[100005];
long long ans;

int main() {
  cin >> N;
  for (int i = 1; i <= N; i++) {
    cin >> A[i];
    A[i] -= i;
  }
  sort(A + 1, A + N + 1);
  for (int i = 1; i <= N; i++) {
    ans += abs(A[i] - A[N / 2 + 1]);
  }
  cout << ans << endl;
  return 0;
}

