  int n;
  scanf("%d", &n);
  ll sum = 0;
  rep(i, n) {
    scanf("%d", &a[i]);
    sum += a[i];
  }

  // initial first cut at 0
  //         second cut at 1
  //         third cut at 2
  int fi = 0, se = 1, thi = 2;
  ll w = a[0], x = a[1], y = a[2], z = sum - w - x - y;
  ll ans = INF;
  for (; se + 2 < n; se++) {
    // printf("%lld %lld %lld %lld\n", w, x, y, z);
