      w += a[fi];
      x -= a[fi];
    }
    while (thi + 1 < n && abs(y + a[thi + 1] - z + a[thi + 1]) < abs(y - z)) {
      thi++;
      y += a[thi];
      z -= a[thi];
    }
    ll mx = max({w, x, y, z});
    ll mi = min({w, x, y, z});
    ans = min(ans, mx - mi);
    x += a[se + 1];
    y -= a[se + 1];
  }
