#include <bits/stdc++.h>
typedef long long ll;
using namespace std;

ll N;
vector<ll> A(200001, 0);
pair<ll, ll> L[100001];
pair<ll, ll> R[100001];

void MakePair(ll tail, ll t_sum, ll h_sum, pair<ll, ll> *P) {
  for (ll head = 1; head < N - 2; ++head) {
    h_sum += A[head];
    while (tail < head - 1 && abs(h_sum - t_sum) > abs((h_sum - A[tail + 1]) -
                                                       (t_sum + A[tail + 1]))) {
      ++tail;
      t_sum += A[tail], h_sum -= A[tail];
    }
    P[head] = make_pair(t_sum, h_sum);
  }
  return;
}

int main(void) {

  cin >> N;
  for (ll i = 0; i < N; ++i)
    cin >> A[i];

  MakePair(0, A[0], 0, L);
  reverse(A.begin(), A.begin() + N);
  MakePair(0, A[0], 0, R);

  ll j = N - 3, ans = LLONG_MAX;
  for (ll i = 1; i < N - 2; ++i) {
    ll mx = max(max(L[i].first, L[i].second), max(R[j].first, R[j].second));
    ll mn = min(min(L[i].first, L[i].second), min(R[j].first, R[j].second));
    --j;
    ans = min(ans, mx - mn);
  }
  cout << ans << endl;

  return 0;
}

