  for (int i = 0; i < N; i++)
    cin >> A[i];
  vector<ll> pref(N + 1);
  for (int i = 0; i < N; i++)
    pref[i + 1] = pref[i] + A[i];
  ll ans = pref[N];
  auto getHalf = [&](const multiset<ll> ms, ll v) {
    auto it = ms.lower_bound((v + 1) / 2);
    ll res = 0;
    if (it != ms.end())
      res = max(res, v - *it);
    if (it != ms.begin())
      res = max(res, *(--it));
    return res;
  };
  multiset<ll> prefs;
  multiset<ll> suffs;
  prefs.insert(pref[1]);
  for (int i = 2; i <= N - 1; i++)
