LL calc(int i) { return sum[x[i]] - sum[x[i - 1]]; }
LL llabs(LL x) { return x < 0 ? -x : x; }
bool cmp(int a, int b) { return calc(a) < calc(b); }
bool cmp2(int a, int b) { return calc(a) > calc(b); }
void move(int id, int d) {
  if (d == 1) {
    if (x[id + 1] - x[id] > 1)
      x[id]++;
  } else {
    if (x[id] - x[id - 1] > 1)
      x[id]--;
  }
}
LL trys() {
  LL ans = 1e17;
  int y[5];
  for (int i = 0; i < 5; i++)
    y[i] = x[i];
  for (int i = 1; i <= 40; i++) {
    int id[5] = {0, 1, 2, 3, 4};
    sort(id + 1, id + 5, cmp);
    ans = min(ans, llabs(calc(id[1]) - calc(id[4])));
    int r = rand() % 6;
    if (r == 0) {
      LL val = calc(1);
      if (val > v)
        move(1, -1);
      else
        move(1, 1);
    }
    if (r == 1) {
      LL val = calc(4);
      if (val > v)
        move(3, 1);
      else
        move(3, -1);
    }
    if (r > 1) {
      r -= 2;
      int k = r & 1 ? 2 : 3;
      r /= 2;
      LL val = calc(k);
      if (r) {
        if (val > v)
          move(k - 1, 1);
        else
          move(k - 1, -1);
      } else {
        if (val > v)
          move(k, -1);
        else
          move(k, 1);
      }
    }
  }
  for (int i = 0; i < 5; i++)
    x[i] = y[i];
  return ans;
}
LL solve() {
  for (int i = 1; i <= n; i++)
    sum[i] = sum[i - 1] + a[i];
  v = sum[n] / 4;
  for (int i = 1; i <= 3; i++) {
    x[i] = x[i - 1] + 1;
    while (x[i] < n + i - 4 && sum[x[i]] - sum[x[i - 1]] <= v)
      x[i]++;
  }
  x[4] = n;
  LL ans = 1e17;
  for (int xxxx = 1; xxxx <= n * 4; xxxx++) {
    ans = min(ans, trys());
    int id[5] = {0, 1, 2, 3, 4};
    sort(id + 1, id + 5, cmp);
    ans = min(ans, llabs(calc(id[1]) - calc(id[4])));
    int j = 1;
    while (j <= 4 && (id[j] == 1 || x[id[j] - 1] - x[id[j] - 2] == 1))
      j++;
    if (j > 4)
      break;
    x[id[j] - 1]--;
  }
  return ans;
}
LL solve2() {
  for (int i = 1; i <= n; i++)
    sum[i] = sum[i - 1] + a[i];
  v = sum[n] / 4;
  for (int i = 1; i <= 3; i++) {
    x[i] = x[i - 1] + 1;
