      j++;
    if (j > 4)
      break;
    x[id[j] - 1]--;
  }
  return ans;
}
int main() {
  srand(19260817);
  scanf("%d", &n);
  for (int i = 1; i <= n; i++)
    scanf("%lld", &a[i]);
  LL ans = 1e17;
  ans = min(ans, solve());
  for (int i = 1; i <= n / 2; i++)
    swap(a[i], a[n - i + 1]);
  ans = min(ans, solve());
  ans = min(ans, solve2());
  for (int i = 1; i <= n / 2; i++)
    swap(a[i], a[n - i + 1]);
  ans = min(ans, solve2());
  printf("%lld", ans);
  return 0;
}

